INSERT INTO EPISODES VALUES('Breaking Bad',1,'Le choix',3,'27-01 -2008 00:42:00','Walt et Jesse décident de cuisiner dans un camping-car au fond du désert. Jesse prend contact avec un revendeur de drogue, Krazy-8, afin d écouler leur petit stock.');
